		function select_text($matches) {
			$values = explode('|',$matches[1]);
			return $values[array_rand($values)];
		}
		function randomStringFromFile($filename) {
			$file = file($filename);
			$rand = mt_rand(0,count($file)-1);
			return trim($file[$rand]); 
